function DeleteOrder(n){bootbox.confirm("Delete product?",function(t){t&&$.ajax({url:"/api/admin/order/"+n,type:"DELETE",success:function(n){$.isSuccess(n)?mainTable.ajax.reload():bootbox.alert(n)}})})}function ApproveOrder(n){$.ajax({url:"/api/admin/order/"+n,type:"PATCH",success:function(n){$.isSuccess(n)?bootbox.alert("Order Approved.",function(){mainTable.ajax.reload()}):bootbox.alert(n)}})}var mainTable;$(function(){mainTable=$("#mainTable").DataTable({responsive:!0,serverSide:!0,searchDelay:500,lengthMenu:[10,15,20],ajax:{url:"/api/admin/orders"},columns:[{data:"orderNo"},{data:"user"},{data:"status",width:30,orderable:!1,render:function(n){if(!$.isNW(n))return $.enumToName(OrderStatusEnum,n)}},{data:"createdOnUtc"},{width:210,orderable:!1,render:function(n,t,i){return'<button class="approveBtn btn btn-sm btn-secondary" type="button" onclick="ApproveOrder(\''+i.orderNo+'\')">Approve<\/button><a class="btn btn-sm btn-primary mr-2 ml-2" href="/admin/order/Detail/'+i.orderNo+'">Detail<\/a><button class="btn btn-sm btn-danger" type="button" onclick="DeleteOrder(\''+i.orderNo+"')\">Delete<\/button>"}}],columnDefs:[{className:"text-center",targets:[-1]},],order:[[3,"DESC"]]})});