function DeleteProduct(n){bootbox.confirm("Delete product?",function(t){t&&$.ajax({url:"/api/admin/product/"+n,type:"DELETE",success:function(n){$.isSuccess(n)?mainTable.ajax.reload():bootbox.alert(n)}})})}function UpdateProductStatus(n,t){$.ajax({url:"/api/admin/product",data:{asin:n,status:t},type:"PATCH",success:function(n){$.isSuccess(n)?mainTable.ajax.reload():bootbox.alert(n)}})}var mainTable;$(function(){mainTable=$("#mainTable").DataTable({serverSide:!0,searchDelay:500,lengthMenu:[5,10,25],ajax:{url:"/api/admin/products"},columns:[{width:50,orderable:!1,render:function(n,t,i){return'<img src="'+$.pic(i.imageUrl)+'" class="pic_s" />'}},{data:"asin"},{data:"productName"},{data:"status",width:30,orderable:!1},{data:"createdOnUtc",width:150,render:function(n){return $.timeFormat(n)}},{width:50,orderable:!1,render:function(n,t,i){return i.status===1?'<button type="button" class="btn btn-sm btn-primary" onclick="UpdateProductStatus(\''+i.asin+"', 0)\">Deactivate<\/button>":'<button type="button" class="btn btn-sm btn-primary" onclick="UpdateProductStatus(\''+i.asin+"', 1)\">Activate<\/button>"}},{width:120,orderable:!1,render:function(n,t,i){return'<a class="btn btn-sm btn-primary mr-2" href="/admin/product/Save/'+i.asin+'">Edit<\/a><button class="delBtn btn btn-sm btn-danger" type="button" onclick="DeleteProduct(\''+i.asin+"')\">Delete<\/button>"}}],columnDefs:[{className:"text-center align-middle",targets:[-1,0,3,5]},{className:"align-middle",targets:"_all"}],order:[[2,"DESC"]]})});