var mainTable;$(function(){mainTable=$("#mainTable").DataTable({serverSide:!0,searchDelay:500,lengthMenu:[5,10,25],ajax:{url:"/api/products"},columns:[{width:50,orderable:!1,render:function(n,t,i){return'<img src="'+$.pic(i.imageUrl)+'" class="pic_s" />'}},{data:"asin"},{data:"productName"},{data:"createdOnUtc",width:150,render:function(n){return $.timeFormat(n)}},{width:120,orderable:!1,render:function(n,t,i){return'<a class="btn btn-sm btn-primary mr-2" href="/admin/product/Save/'+i.asin+'">Edit<\/a><button class="delBtn btn btn-sm btn-danger" type="button" data-id="'+i.asin+'">Delete<\/button>'}}],columnDefs:[{className:"text-center align-middle",targets:[-1,0]},{className:"align-middle",targets:"_all"}],order:[[2,"DESC"]]});$("#mainTable").on("click",".delBtn",function(){var n=$(this);bootbox.confirm("Delete product?",function(t){if(t){var i=n.data("id");$.ajax({url:"/api/product/"+i,type:"DELETE",success:function(n){$.isSuccess(n)?mainTable.ajax.reload():bootbox.alert(n)}})}})})});