var mainTable;$(function(){mainTable=$("#mainTable").DataTable({serverSide:!0,searchDelay:500,lengthMenu:[5,10,25],ajax:{url:"/api/product/items"},columns:[{width:50,orderable:!1,render:function(n,t,i){return'<img src="'+$.pic(i.imageUrl)+'" class="pic_s" />'}},{data:"itemNo"},{data:"productName"},{data:"createdOnUtc",width:150,render:function(n){return $.timeFormat(n)}},{width:120,orderable:!1,render:function(n,t,i){return'<a class="btn btn-sm btn-primary mr-2" href="/admin/product/Save/'+i.itemNo+'">Edit<\/a><button class="delBtn btn btn-sm btn-secondary" type="button" data-id="'+i.itemNo+'">Delete<\/button>'}}],columnDefs:[{className:"text-center align-middle",targets:[-1,0]},{className:"align-middle",targets:"_all"}],order:[[2,"DESC"]]});$("#mainTable").on("click",".delBtn",function(){var n=$(this);bootbox.confirm("Delete item?",function(t){if(t){var i=n.data("id");$.ajax({url:"/api/product/item/"+i,type:"DELETE",success:function(n){$.isSuccess(n)?window.location="/admin/product":bootbox.alert(n)}})}})})});